#ifdef _WIN32

// htslib uses these POSIX/linux functions.  We provide declarations
// here and We provide implementations in compat/, derived from other
// LGPL- or BSD-licensed implementations.

#include <stddef.h>
#include <direct.h>

double drand48();
void* valloc(size_t size);
int fsync(int fd);
int getpagesize(void);

#undef mkdir
#define mkdir(name, mode) _mkdir (name)

// vsnprintf is "different" on windows.  it fails (-1; errno ->
// ERANGE) when buffer has inadequate space, doesn't indicate size of
// entire formatted string
#define vsnprintf __mingw_vsnprintf

#endif // _WIN32
